import './styles.css'
import { useState  } from 'react'
import Tile from './components/tile.js'

const Grid = ({ grid, width, revealTile, flagTile }) => (
  <div className="grid" style={{gridTemplateColumns: `repeat(${width}, auto)`}}>
    {grid.map( tile =>
      <Tile 
        key={tile.id}
        revealTile={ revealTile }
        flagTile={flagTile}
        tile={tile} />
    )}
  </div>
)
const openAdjacentEmptyTiles = (tile) => {
  const adjacentTiles = getAdjacentIndexes(tile.x, tile.y, tile.idx, width)
}
const getAdjacentIndexes = (x, y, idx, width, height) => {
  const topL = idx - (width+1)
  const top = idx - width
  const topR = idx - (width-1)
  const L = idx - 1
  const R = idx + 1
  const botL = idx + (width-1)
  const bot= idx + width
  const botR = idx + (width+1)
  const neighborIndexes = []

  if (x === 0){
    if (y === 0){
      neighborIndexes.push(R, bot, botR)     // left top corner
    } else if (y === height - 1){
      neighborIndexes.push(R, top, topR)     // left bot corner
    } else {
      neighborIndexes.push(top, topR, R, bot, botR)   // left side
    }
  } else if (x === width - 1){
    if (y === 0){
      neighborIndexes.push(L, bot, botL)    // right top corner
    } else if (y === height - 1){
      neighborIndexes.push(L, top, topL)    // right bot corner
    } else {
      neighborIndexes.push(top, topL, L, bot, botL)   // right side
    }
  } else if (y === 0){
    neighborIndexes.push(L, botL, bot, botR, R)   // top
  } else if (y === height - 1){
    neighborIndexes.push(L, topL, top, topR, R)   // bot
  } else {
    neighborIndexes.push(topL, top, topR, L, R, botL, bot, botR)    // middle
  }

  return neighborIndexes
}
const getAdjacentMineCount = (x, y, idx, mineField, width, height) => {
  let count = 0
  if (mineField[idx]) return 0

  const neighborIndexes = getAdjacentIndexes(x, y, idx, width, height)

  neighborIndexes.forEach((idx) => {
    if (mineField[idx]) {
      count++
    }
  })
  return (count > 0) ? count : 0
}
const generateGrid = (width, height, mineField) => {
  let newGrid = []
  console.log('width, height, mineField:',width, height, mineField)
  for (let i = 0; i < height; ++i){
    for (let j = 0; j < width; ++j) {
      const idx = j + width*i
      const tile = {
        id: `${i}_${j}`,
        idx: idx,
        x: j,
        y: i,
        status: 'hidden',
        adjacentMines: getAdjacentMineCount(j, i, idx, mineField, width, height)
      }
      newGrid.push(tile)
    }
  }
  return newGrid
}
const randInt = (min, max) => {
  return Math.floor(Math.random() * (max - min)) + min;
} 
const generateMineField = (mineCount, gridLen) => {
  if ( mineCount > gridLen ) {
    console.log('Too many mines!')
    return -1
  }
  let newMineField = new Array(gridLen).fill(false)
  let mineIndexes = []
  while (mineIndexes.length < mineCount) {
    const randomNumber = randInt(0, gridLen)
    if (!mineIndexes.includes(randomNumber)) {
      mineIndexes.push(randomNumber)
    }
  }
  mineIndexes.forEach((idx) => newMineField[idx] = true)
  return newMineField
}

function App() {
  const [grid, setGrid] = useState([])
  const [mineField, setMineField] = useState([])
  const [width, setWidth] = useState(9)
  const [height, setHeight] = useState(9)
  const [mineCount, setMineCount] = useState(10)
  const [gameOverText, setGameOverText] = useState('') 

  const initializeBoard = () => {
    setGameOverText('')

    const newMineField = generateMineField(mineCount, width*height)
    setMineField(newMineField)

    let newGrid = generateGrid(width, height, newMineField)
    setGrid(newGrid)
  }

  // Game does not continue if:
  // 1. A tile has a mine, and it's unopened
  // 2. A tile does NOT have a mine and it's unopened
  const checkForWin = () => {
    for (const tile of grid){
      const tileHasMine = mineField[tile.idx]
      const tileIsOpened = tile.status.includes('open') || tile.status.includes('mine')

      if (tileHasMine && tileIsOpened) return false
      if (!tileHasMine && !tileIsOpened) return false
    }
    return true
  }

  // Call this when game is won:
  // 1. Mark flagged mines as correct
  // 2. Disable all tiles
  const win = (editableGrid) => {
    editableGrid.forEach( (tile) => {
      if (tile.status.includes('disabled') ) return

      if ( mineField[tile.idx] ) {
        tile.status = 'correct' 
      }
      tile.status = tile.status + ' disabled'
    })
    setGameOverText('Congratulations!')
  }
  // Call when game is lost
  // 1. Mark correct flags as correct
  // 2. Mark wrong flags as wrong (TODO)
  // 3. Reveal all mines
  // 3. Disable all tiles
  const gameOver = (editableGrid) => {
    editableGrid.forEach( (tile) => {
      if (tile.status.includes('disabled') ) return

      if ( mineField[tile.idx] ) {
        tile.status = (tile.status === 'flag') ?
        'correct' : 'mine'
      }
      tile.status = tile.status + ' disabled'
    })
    setGameOverText('Game over!')
  }

  const revealTile = (tile) => {
    let newGrid = [...grid]
    if (mineField[tile.idx]) {
      // GAME OVER :()
      gameOver(newGrid, 'Game Over!')
    } else {
      // reveal number / empty tile
      if (newGrid[tile.idx].adjacentMines === 0){
        console.log('empty tile :)')
        openAdjacentEmptyTiles(tile)
      }

      newGrid[tile.idx].status = 'open'
      setGrid(newGrid)
    }

    if (checkForWin()){
      win(newGrid)
    }
  }

  const flagTile = (tile) => {
    let newGrid = [...grid]
    switch (tile.status){
      case 'hidden':
        newGrid[tile.idx].status = 'flag'
        setGrid(newGrid)
        break

      case 'flag':
        newGrid[tile.idx].status = 'hidden'
        setGrid(newGrid)
        break
      default:
        break
    }

  }

  return (
    <>
      <div className="header">
        <h1>Miinaharava</h1>
        <label htmlFor="width">width</label>
        <input
          value={width}
          onChange={e => setWidth(e.target.value)}
          type="text"
          id="width"
        />
        <label htmlFor="height">height</label>
        <input
          value={height}
          onChange={e => setHeight(e.target.value)}
          type="text"
          id="height"/>
        <label htmlFor="mineCount">mineCount</label>
        <input
          value={mineCount}
          onChange={e => setMineCount(e.target.value)}
          type="text"
          id="mineCount"/>
        <button
          onClick={(e) => {initializeBoard()}}
          > New Game </button>
      </div>
      <div className="body">
        <div className="gameOverText">
          {gameOverText}
        </div>
        <Grid 
            grid={grid} 
            width={width} 
            revealTile={revealTile}
            flagTile={flagTile}
          />
      </div>



    </>
  );
}

export default App;





