import './styles.css'
const generateGrid = (width, height, mines, reveal) =>{
  let grid = []

  console.log(width, height, mines, reveal)

  for (let i = 0; i < width; ++i){
    for (let j = 0; j < height; ++j) {
      let id = `${j}_${i}`
      grid.push(<div className={"square "+id} onClick={() => reveal(id)}></div>)
    }
  }
  console.log("grid:", grid)
  return grid
}

function App() {
  const width = 3
  const height = 3
  const mines = 1
  const grid = generateGrid(width, height, mines)

  const reveal = id => {
    console.log(id === "3_1")
    return id === "3_1"
  }

  return (
    <>
      <h1>Miinaharava</h1>
      <div className="container">

        {grid.map()}

        <div className="square 1_1" onClick={() => reveal("1_1")}></div>
        <div className="square 1_2"></div>
        <div className="square 1_3"></div>
        <div className="square 2_1"></div>
        <div className="square 2_2"></div>
        <div className="square 2_3"></div>
        <div className="square 3_1"></div>
        <div className="square 3_2"></div>
        <div className="square 3_3"></div>
      </div>



    </>
  );
}

export default App;
