  //   classList.remove('flag')
  //   classList.add('hidden')
  // } else {
  //   classList.remove('hidden')
  //   classList.add('flag')
  // }

  const handleClick = (e) => {
    e.preventDefault()

    const checkForMine = () => {
      if (mineField[idx]) {
        //end game
        classList.remove('hidden')
        classList.add('mine')
      } else {
        // reveal number / empty
        classList.remove('hidden')
        classList.add('open')
      }
    }

    var square = e.target
    var classList = square.classList
    var idx = indexFromId(square.id)

    switch (true){
      // LEFT CLICK:
      case ((e.type === 'click') && classList.contains('hidden')):
        checkForMine()
        break
      case ((e.type ==='click') && classList.contains('flag')):
