import './styles.css'
import { useState, useEffect  } from 'react'
import Square from './components/square.js'

const Grid = ({ grid, width, revealSquare }) => (
  <div className="grid" style={{gridTemplateColumns: `repeat(${width}, auto)`}}>
    {grid.map( square => 
      <Square key={square.id}
        revealSquare={ revealSquare }
        square={square} />
    )}
  </div>
)

const generateGrid = (width, height) => {
  let g = []

  for (let i = 0; i < height; ++i){
    for (let j = 0; j < width; ++j) {
      let id = `${i}_${j}`
      g.push({
        id: id,
        classes: "square hidden"
      })
    }
  }
  return g
}
const randInt = (min, max) => {
  return Math.floor(Math.random() * (max - min)) + min;
}
// Returns a boolean array with length of gridLen, with mineCount trues 
const generateMineField = (mineCount, gridLen) => {
  if ( mineCount > gridLen ) {
    console.log('Too many mines!')
    return -1
  }
  let newMineField = new Array(gridLen).fill(false)
  let mineIndexes = []
  while (mineIndexes.length < mineCount) {
    const randomNumber = randInt(0, gridLen)
    if (!mineIndexes.includes(randomNumber)) {
      mineIndexes.push(randomNumber)
    }
  }
  mineIndexes.forEach((idx) => newMineField[idx] = true)
  return newMineField
}

const indexFromId = (id, width) => {
  const parts = id.split('_')
  return parseInt(parts[0])*width + parseInt(parts[1])
}


function App() {
  const [grid, setGrid] = useState([])
  const [mineField, setMineField] = useState([])
  const [width, setWidth] = useState(4)
  const [height, setHeight] = useState(5)
  const [mineCount, setMineCount] = useState(3)

  const createEmptyGrid = () => {
    const newGrid = generateGrid(width, height)
    setGrid(newGrid)

    const newMineField = generateMineField(mineCount, width*height)
    setMineField(newMineField)
  }

  const revealSquare = (square) => {
    console.log('square:', square)
  }

  return (
    <>
      <div className="header">
        <h1>Miinaharava</h1>
        <label htmlFor="width">width</label>
        <input
          value={width}
          onChange={e => setWidth(e.target.value)}
          type="text"
          id="width"
        />
        <label htmlFor="height">height</label>
        <input
          value={height}
          onChange={e => setHeight(e.target.value)}
          type="text"
          id="height"/>
        <button
          onClick={(e) => {createEmptyGrid()}}
          > New Game </button>
      </div>

      <Grid grid={grid} width={width} revealSquare={revealSquare}/>

    </>
  );
}

export default App;





