export default function Tile({ revealTile, flagTile, tile }) {

  const handleClick = (e) => {
    e.preventDefault()
    if (tile.status.includes('disabled')) return

    if (e.type === 'click') {
      revealTile(tile)
    } else if (e.type === 'contextmenu') {
      flagTile(tile)
    }
  }

  return (
    <div
      className={'tile ' + tile.status} 
      id={tile.id}
      onClick={handleClick} 
      onContextMenu={handleClick}>

      {/* <TileNumber visible={tile.status === 'open'} number={tile.adjacentMines}></TileNumber> */}
    </div> 
  )
}
