import './styles.css'
import { useState } from 'react'

const Square = ({handleClick, id, idx}) => ( 
  <div className={"square hidden " + id} 
      onClick={() => handleClick(idx, id)} /> 
)

const Grid = ({ grid, width }) => {
  return (
    <div className="grid" style={{gridTemplateColumns: `repeat(${width}, auto)`}}>
      {grid.map( square => 
        <Square key={square.id} handleClick={ square.handleClick } id={square.id} idx={square.index} />
      )}
    </div>
  )
}

const generateGrid = (width, height, handleClick) => {
  let grid = []

  for (let i = 0; i < height; ++i){
    for (let j = 0; j < width; ++j) {
      let id = `${i}_${j}`
      let idx = i*width + j
      grid.push({
        id: id,
        index: idx,
        handleClick: handleClick
      })
    }
  }
  return grid
}
const randInt = (min, max) => {
  return Math.floor(Math.random() * (max - min)) + min;
}
const generateMineField = (minesCount, gridLen) => {
  if ( minesCount > gridLen ) {
    console.log('Too many mines!')
    return -1
  }
  let mineField = new Array(gridLen).fill(false)
  let mineIndexes = []
  while (mineIndexes.length < minesCount) {
    const randomNumber = randInt(0, gridLen)
    if (!mineIndexes.includes(randomNumber)) {
      mineIndexes.push(randomNumber)
    }
  }
  mineIndexes.forEach((idx) => mineField[idx] = true)
  return mineField
}

function App() {
  const [width] = useState(3)   // setWidth
  const [height] = useState(4)  // setHeight
  const minesCount = 6

  const mineField = generateMineField(minesCount, width*height)

  const handleClick = (idx, id) => {
    var element = document.getElementsByClassName(id)[0]

    if (mineField[idx]) {
      //end game
      element.classList.remove('hidden')
      element.classList.add('mine')
    } else {
      // reveal number / empty
      element.classList.remove('hidden')
      element.classList.add('open')
    }
  }
  const grid = generateGrid(width, height, handleClick)

  return (
    <>
      <div className="header">
        <h1>Miinaharava</h1>
      </div>

      <Grid grid={grid} width={width}/>

    </>
  );
}

export default App;
