import './styles.css'
import { useState } from 'react'

const generateGrid = (width, height, reveal) => {
  let grid = []
  for (let i = 0; i < height; ++i){
    for (let j = 0; j < width; ++j) {
      let id = `${i}_${j}`
      grid.push(<div key={id} className={"square "+id} onClick={() => reveal(id)}></div>)
    }
  }
  return grid
}


const randInt = (min, max) => {
  return Math.floor(Math.random() * (max - min)) + min;
}
const generateMines = (minesCount, mineField, setMineField) => {
  if (minesCount > mineField.length) { 
    console.log('too many mines!')
    return -1
  }

  let minesleft = minesCount
  while (minesleft > 0) {
    let idx = randInt(0, mineField.length - 1)
    console.log('mineField[]', mineField[idx])
      if ( !mineField[idx] ) {
      const newMineField = mineField.map((mine, i) => {
        if (i === idx) {
          return true
        } else {
          return mine
        }
      });
      setMineField(newMineField);
      minesleft -= 1
    }
     minesleft -= 1
  }

}

function App() {
  const [width] = useState(3)   // setWidth
  const [height] = useState(4)  // setHeight
  const minesCount = 1

  const reveal = id => {
    // Remove "hidden", add "open" from square with id=id!
  }
  const grid = generateGrid(width, height, reveal)
  const [mineField, setMineField] = useState(
    new Array(grid.length).fill(false)
  )

  console.log('Start generating mines')
  generateMines(minesCount, mineField, setMineField)
  console.log('Mines generated')
  console.log('mineField:', mineField)

  return (
    <>
      <div className="header">
        <h1>Miinaharava</h1>
      </div>

      <div className="grid" style={{gridTemplateColumns: `repeat(${width}, auto)`}}>
        { grid }
      </div>



    </>
  );
}

export default App;
