import './styles.css'
import { useState } from 'react'

const generateGrid = (width, height, reveal) => {
  let grid = []
  for (let i = 0; i < height; ++i){
    for (let j = 0; j < width; ++j) {
      let id = `${i}_${j}`
      grid.push(<div key={id} className={"square "+id} onClick={() => reveal(id)}></div>)
    }
  }
  return grid
}

const generateMines = (mines, minesCount, setMines) => {

  if (minesCount > mines.length) { return -1 }

  let minesleft = minesCount
  while (minesleft) {

    minesleft -1
  }
}


function App() {
  const [width] = useState(3)   // setWidth
  const [height] = useState(4)  // setHeight
  const minesCount = 1

  const reveal = id => {
    // Remove "hidden", add "open" from square with id=id!
  }

  const grid = generateGrid(width, height, reveal)
  const [mines, setMines] = useState(
    new Array(grid.length).fill(false)
  )




  generateMines(minesCount, setMines)

  return (
    <>
      <div className="header">
        <h1>Miinaharava</h1>
      </div>

      <div className="grid" style={{gridTemplateColumns: `repeat(${width}, auto)`}}>
        { grid }
      </div>



    </>
  );
}

export default App;
