import Timer from './components/timer.js'
import Header from './components/header.js'
import GameOverBox from './components/GameOverBox.js'

const Game = () => {

  return (
    <>
      <Header
        width={width} height={height} mineCount={mineCount}
        functions={[
          setWidth,
          setHeight,
          setMineCount,
          initializeGrid
        ]}
      />
      <div className="body">
        <Timer time={time} minesLeft={minesLeftText} />

        <GameOverBox
          text={gameOverText}
          functions={[
            initializeGrid,
            saveScore
          ]} />
        <Grid
          grid={grid}
          functions={
            [ revealTile,
              flagTile,
              checkFlagsAndRevealAdjacentTiles ]}
        />
      </div>
    </>
  )
}

export default Game
