const TileNumber = ({ tile  }) => {
  return tile.status.includes('open') ? (
    <div className="tileNumber">
      { (tile.adjacentMines > 0) ? tile.adjacentMines : ''}
    </div>
  ) : ''
}

export default function Tile({ revealTile, flagTile, tile }) {

  const handleClick = (e) => {
    e.preventDefault()
    if (tile.status.includes('disabled')) return


    if (e.type === 'click') {
      switch (tile.status){
        case 'hidden':
          revealTile(tile)
          break
        default:
          break
      }
    } else if (e.type === 'contextmenu') {
      flagTile(tile)
    }
  }

  return (
    <div
      className={'tile ' + tile.status} 
      id={tile.id}
      onClick={handleClick} 
      onContextMenu={handleClick}>

      <TileNumber tile={tile}/>
    </div> 
  )
}
