import './styles.css'
import { useState } from 'react'

const generateGrid = (width, height, reveal) => {
  let grid = []
  for (let i = 0; i < height; ++i){
    for (let j = 0; j < width; ++j) {
      let id = `${i}_${j}`
      grid.push(<div key={id} className={"square "+id} onClick={() => reveal(id)}></div>)
    }
  }
  return grid
}


const randInt = (min, max) => {
  return Math.floor(Math.random() * (max - min)) + min;
}
const generateMineField = (minesCount, gridLen) => {
  if ( minesCount > gridLen ) {
    console.log('Too many mines!')
    return -1
  }

  let mineField = new Array(gridLen).fill(false)
  let mineIndexes = []

  while (mineIndexes.length < minesCount) {
    const randomNumber = randInt(0, gridLen)
    if (!mineIndexes.includes(randomNumber)) {
      mineIndexes.push(randomNumber)
    }
  }

  mineIndexes.forEach((idx) => mineField[idx] = true)

  return mineField
}

function App() {
  const [width] = useState(3)   // setWidth
  const [height] = useState(4)  // setHeight
  const minesCount = 5
  const mineField = generateMineField(minesCount, width*height)

  const reveal = id => {
    // Remove "hidden", add "open" from square with id=id!
  }
  const grid = generateGrid(width, height, reveal)


  return (
    <>
      <div className="header">
        <h1>Miinaharava</h1>
      </div>

      <div className="grid" style={{gridTemplateColumns: `repeat(${width}, auto)`}}>
        { grid }
      </div>

    </>
  );
}

export default App;
