import './styles.css'
import { useState, useEffect  } from 'react'
import Tile from './components/tile.js'
import Timer from './components/timer.js'

const Grid = ({ grid, width, revealTile, flagTile, checkFlagsAndRevealAdjacentTiles }) => {
  return (
  <div className="grid" style={{gridTemplateColumns: `repeat(${width}, auto)`}}>
    {grid.map( tile =>
      <Tile 
        key={tile.id}
        tile={tile} 
        revealTile={ revealTile }
        flagTile={flagTile}
        checkFlagsAndRevealAdjacentTiles={checkFlagsAndRevealAdjacentTiles}
        />
    )}
  </div>
)}

const getXYfromIndex = (index, width) => {
  const x = index%width
  const y = Math.floor(index/width)
  return { x, y }
}
const getAdjacentTileIndexes = (idx, width, height)  => {
  const { x, y } = getXYfromIndex(idx, width)

  const topL = idx - (width + 1)
  const top = idx - width
  const topR = idx - (width - 1)
  const L = idx - 1
  const R = idx + 1
  const botL = idx + (width - 1)
  const bot= idx + width
  const botR = idx + (width + 1)
  const neighborIndexes = []

  if (x === 0){
    if (y === 0){
      neighborIndexes.push(R, bot, botR)     // left top corner
    } else if (y === height - 1){
      neighborIndexes.push(R, top, topR)     // left bot corner
    } else {
      neighborIndexes.push(top, topR, R, bot, botR)   // left side
    }
  } else if (x === width - 1){
    if (y === 0){
      neighborIndexes.push(L, bot, botL)    // right top corner
    } else if (y === height - 1){
      neighborIndexes.push(L, top, topL)    // right bot corner
    } else {
      neighborIndexes.push(top, topL, L, bot, botL)   // right side
    }
  } else if (y === 0){
    neighborIndexes.push(L, botL, bot, botR, R)   // top
  } else if (y === height - 1){
    neighborIndexes.push(L, topL, top, topR, R)   // bot
  } else {
    neighborIndexes.push(topL, top, topR, L, R, botL, bot, botR)    // middle
  }

  return neighborIndexes
}
const getAdjacentMineCount = (idx, mineField, width, height) => {
  let count = 0
  if (mineField[idx]) return 0

  const neighborIndexes = getAdjacentTileIndexes(idx, width, height)

  neighborIndexes.forEach((idx) => {
    if (mineField[idx]) {
      count++
    }
  })

  return (count > 0) ? count : null
}
const generateGrid = (wid, hgt, mineField) => {
  let newGrid = []
  for (let i = 0; i < hgt; ++i){
    for (let j = 0; j < wid; ++j) {
      const idx = j + wid*i
      const tile = {
        id: `${i}_${j}`,
        idx: idx,
        status: 'hidden',
        disabled: false,
        adjacentMines: getAdjacentMineCount(idx, mineField, wid, hgt)
      }
      newGrid.push(tile)
    }
  }
  return newGrid
}
const randInt = (min, max) => {
  return Math.floor(Math.random() * (max - min)) + min;
}
const generateMineField = (mineCount, gridLen) => {
  if ( mineCount > gridLen ) {
    console.log('Too many mines!')
    return -1
  }
  let newMineField = new Array(gridLen).fill(false)
  let mineIndexes = []

  while (mineIndexes.length < mineCount) {
    const randomNumber = randInt(0, gridLen)
    if (!mineIndexes.includes(randomNumber)) {
      mineIndexes.push(randomNumber)
    }
  }
  mineIndexes.forEach((idx) => newMineField[idx] = true)
  return newMineField
}

function App() {
  const [firstClick, setFirstClick] = useState(true)
  const [height, setHeight] = useState(9)
  const [width, setWidth] = useState(9)
  const [mineCount, setMineCount] = useState(10)
  const [grid, setGrid] = useState([])
  const [mineField, setMineField] = useState([])
  const [gameOverText, setGameOverText] = useState('')
  const [time, setTime] = useState(0)
  const [timerOn, setTimerOn] = useState(0)

  useEffect(() => {
    let interval = null

    if (timerOn) {
      interval = setInterval(() => {
        setTime(prevTime => prevTime + 1)
      }, 10)
    } else {
      clearInterval(interval)
    }

    return () => clearInterval(interval)
  }, [timerOn]);


  // Game does not continue if:
  // 1. A tile has a mine, and it's unopened
  // 2. A tile does NOT have a mine and it's unopened
  const checkForWin = () => {
    for (const tile of grid){
      const tileHasMine = mineField[tile.idx]
      const tileIsOpened = tile.status.includes('open') || tile.status.includes('mine')

      if (tileHasMine && tileIsOpened) return false
      if (!tileHasMine && !tileIsOpened) return false
    }
    return true
  }

  // Call this when game is won:
  // 1. Reveal all non-flagged mines
  // 2. Disable all tiles
  const win = (editableGrid) => {
    setTimerOn(false)
    editableGrid.forEach( (tile) => {
      if ( tile.disabled ) return

      if ( mineField[tile.idx] && tile.status !== 'flag' ) {
        tile.status = 'mine' 
      }
      tile.disabled = true
    })
    setGameOverText('Congratulations!')
  }
  // Call when game is lost
  // 1. Mark wrong flags as wrong
  // 2. Reveal all mines (keep flagged mines as flags)
  // 3. Disable all tiles
  const gameOver = (editableGrid) => {
    setTimerOn(false)
    editableGrid.forEach( (tile) => {
      if ( !mineField[tile.idx] && tile.status === 'flag') {
        tile.status = 'flag_incorrect'
      }
      if ( mineField[tile.idx] && tile.status !== 'flag') {
        tile.status = 'mine'
      }

      tile.disabled = true
    })
    setGameOverText('Game over!')
  }
  // If an open tile is clicked, and all it's surrounding
  // mines have been flagged, reveal all remaining tiles.
  // However, if you place the correct number of flags on
  // the wrong squares, this will explode the mines.
  const checkFlagsAndRevealAdjacentTiles = (tile) => {
    const neighbors = getAdjacentTileIndexes(tile.idx, width, height)
    let neighbouringFlags = 0

    neighbors.forEach((idx) => {
      const tile = grid[idx]
      if (tile.status === 'flag'){
        neighbouringFlags++
      }
    })

    if (neighbouringFlags === tile.adjacentMines){
      revealAdjacentHiddenTiles(tile)
    }
  }
  const revealAdjacentHiddenTiles = (tile) => {
    const neighbors = getAdjacentTileIndexes(tile.idx, width, height)
    neighbors.forEach((idx) => {
      const tile = grid[idx]
      if (tile.status === 'hidden'){
        revealTile(tile)
      }
    })
  }
  const revealTile = (tile) => {
    if (firstClick) {
      setTimerOn(true)
      setFirstClick(false)
    }
    let newGrid = [...grid]
    if (mineField[tile.idx]) {
      // End game
      gameOver(newGrid)
      tile.status = 'mine_exploded'
    } else {
      // reveal number / empty
      newGrid[tile.idx].status = 'open'
      if (!tile.adjacentMines) {
        revealAdjacentHiddenTiles(tile)
      }
    }

    setGrid(newGrid)
    if (checkForWin()){
      win(newGrid)
    }
  }
  const flagTile = (tile) => {
    let newGrid = [...grid]
    switch (tile.status){
      case 'hidden':
        newGrid[tile.idx].status = 'flag'
        break
      case 'flag':
        newGrid[tile.idx].status = 'hidden'
        break
      default:
        break
    }
    setGrid(newGrid)
  }

  const initializeGrid = () => {
    setTimerOn(false)
    setTime(0) 
    setFirstClick(true)
    setGameOverText('')
    const newMineField = generateMineField(mineCount, width*height)
    const newGrid = generateGrid(width, height, newMineField)
    setMineField(newMineField)
    setGrid(newGrid)
  }


  document.

  return (
    <>
      <div className="header">
        <h1>Miinaharava</h1>

        <label htmlFor="width">width</label>
        <input
          value={width}
          onChange={e => setWidth(Number(e.target.value))}
          type="text"
          id="width"/>

        <label htmlFor="height">height</label>
        <input
          value={height}
          onChange={e => setHeight(Number(e.target.value))}
          type="text"
          id="height"/>

        <label htmlFor="mineCount">mineCount</label>
        <input
          value={mineCount}
          onChange={e => setMineCount(Number(e.target.value))}
          type="text"
          id="mineCount"/>

        <button
          onClick={initializeGrid}
          > New Game </button>
      </div>


      <div className="body">
        <Timer time={time} />

        <div className="gameOverText">
          {gameOverText}
        </div>
        <Grid
            grid={grid}
            width={width}
            revealTile={revealTile}
            flagTile={flagTile}
            checkFlagsAndRevealAdjacentTiles={checkFlagsAndRevealAdjacentTiles}
          />
      </div>
    </>
  );
}

export default App;
