export default function Tile({ revealTile, flagTile, tile }) {

  const handleClick = (e) => {
    e.preventDefault()
    if (tile.status.includes('disabled')) return

    if (e.type === 'click') {
      switch (tile.status){
        case 'hidden':
          revealTile(tile)
          break
        default:
          console.log('tile not hidden, dont do shit')
          break
      }

      revealTile(tile)
    } else if (e.type === 'contextmenu') {
      flagTile(tile)
    }
  }

  return (
    <div
      className={'tile ' + tile.status} 
      id={tile.id}
      onClick={handleClick} 
      onContextMenu={handleClick}>

      <div className="tileNumber">
        {tile.adjacentMines}
      </div>
    </div> 
  )
}
