const GameOverBox = ({ text }) => {

  if (text === ''){ return null }
  else {
    const buttons = text==='Game over!' ? (
      <button>Play again?</button>
    ) : (
      <>
        Save score to leaderboard?
        <button>Save</button>
      </>
    )

  }


  (
    <div className="gameOverBox">
      <div className="gameOverText">
        {text}
      </div>
      buttons
    </div>
  )

}

export default GameOverBox
