const TileNumber = ({ tile  }) => {
  return tile.status.includes('open') ? (
    <div className={'nro_' + tile.adjacentMines} />

  ) : ''
}
export default function Tile({ tile, revealTile, flagTile, checkFlagsAndRevealAdjacentTiles }) {

  const handleClick = (e) => {
    e.preventDefault()
    if (tile.disabled) return


    if (e.type === 'click') {
      switch (tile.status){
        case 'hidden':
          revealTile(tile)
          break
        case 'open':
          checkFlagsAndRevealAdjacentTiles(tile)  
          break
        default:
          break
      }
    } else if (e.type === 'contextmenu') {
      flagTile(tile)
    }
  }
  const nroClass = tile.status === 'open'
  return (
      <div
          className={'tile ' + tile.status + nroClass + (tile.disabled ? ' disabled' : '')} 
          id={tile.id}
          onClick={handleClick} 
          onContextMenu={handleClick}>
      </div>
  )
}
