    </div>
  )
}

const generateGrid = (width, height, mines, reveal) => {
  let grid = []

  for (let i = 0; i < height; ++i){
    for (let j = 0; j < width; ++j) {
      let id = `${i}_${j}`
      grid.push(<div key={id} className={"square "+id} onClick={() => reveal(id)}></div>)
    }
  }

  return grid
}

function App() {
  const [width, setWidth] = useState(3)
  const [height, setHeight] = useState(3)
  const mines = 1

  const reveal = id => {
    console.log(id === "2_0")
  }

  const grid = generateGrid(width, height, mines, reveal)


  return (
    <>
      <h1>Miinaharava</h1>
      <div className="grid" style={{gridTemplateColumns: `repeat(${width}, auto)`}}>

        { grid.map((square) => square) }

      </div>



    </>
  );
}

export default App;
