import Leaderboard from '../components/leaderboard.js'
import scoreService from '../services/scores'
import { useLoaderData } from 'react-router-dom'

const Beginner = ({ initializeGrid }) => {
  const topScores = useLoaderData()
  return (
    <>
      <Leaderboard scores={topScores}/>
      <div style={{ marginInline:"auto", width: "fit-content" }} className="pb-4">
        <button style={{width:"150px"}}
          onClick={ initializeGrid }
          > New Game / Refresh</button>
      </div>
    </>
  )
}

export const beginnerLoader = async () => {
  console.log('beginnerloader')
  const score = await scoreService.getBeginner()
    .then(scores => {
      scores.sort((a, b) => a.time - b.time)
      return scores
    })
  return score
}

export default Beginner
