import './styles.css'
import { useState } from 'react'

const GridForm = ({ grid, setHeight, setWidth }) => {


}

const generateGrid = (width, height, mines, reveal) => {
  let grid = []

  for (let i = 0; i < height; ++i){
    for (let j = 0; j < width; ++j) {
      let id = `${i}_${j}`
      grid.push(<div key={id} className={"square "+id} onClick={() => reveal(id)}></div>)
    }
  }

  return grid
}

function App() {
  const [width] = useState(3)   // setWidth
  const [height] = useState(4)  // setHeight
  const [mines] = useState(1)   // setMines

  const reveal = id => {
    console.log(id === "2_0")
  }

  const grid = generateGrid(width, height, mines, reveal)

  return (
    <>
      <h1>Miinaharava</h1>
      <div className="grid" style={{gridTemplateColumns: `repeat(${width}, auto)`}}>

        { generateGrid(width, height, mines, reveal) }

      </div>



    </>
  );
}

export default App;
