const TileNumber = ({ tile  }) => {
  // tile.status.includes('open')
  return true ? (
    <div className="tileNumber">
      {tile.adjacentMines}
    </div>
  ) : ''
}

export default function Tile({ revealTile, flagTile, tile }) {

  const handleClick = (e) => {
    e.preventDefault()
    if (tile.status.includes('disabled')) return

    console.log('idx: ', tile.idx)
    console.log('x, y: ', tile.x, ',', tile.y)

    if (e.type === 'click') {
      switch (tile.status){
        case 'hidden':
          revealTile(tile)
          break
        default:
          break
      }
    } else if (e.type === 'contextmenu') {
      flagTile(tile)
    }
  }

  return (
    <div
      className={'tile ' + tile.status} 
      id={tile.id}
      onClick={handleClick} 
      onContextMenu={handleClick}>

      <TileNumber tile={tile}/>
    </div> 
  )
}
