const Content = ({ parts }) => {
  return (
    <div>
      {parts.map(
        part =>
          <Part key={part.id} name={part.name} exercises={part.exercises} />
      )}
    </div>
  )
}

export default function Tile({ revealTile, flagTile, tile }) {

  const handleClick = (e) => {
    e.preventDefault()
    if (tile.status.includes('disabled')) return

    if (e.type === 'click') {
      switch (tile.status){
        case 'hidden':
          revealTile(tile)
          break
        default:
          break
      }
    } else if (e.type === 'contextmenu') {
      flagTile(tile)
    }
  }

  return (
    <div
      className={'tile ' + tile.status} 
      id={tile.id}
      onClick={handleClick} 
      onContextMenu={handleClick}>
      {tile.status === 'open'}
      <div className="tileNumber">
        {tile.adjacentMines}
      </div>
    </div> 
  )
}
