export const scoreLoader = async ({ params }) => {
  const { difficulty } = params

  const fastestFirst = (a, b) => a.time - b.time
  let score;
  switch (difficulty) {
    case 'beginner':
      score = await scoreService.getBeginner()
      .then(scores => {
        scores.sort(fastestFirst)
        return scores
      })
      break;
    case 'intermediate':
      score = await scoreService.getIntermediate()
      .then(scores => {
        scores.sort(fastestFirst)
        return scores
      })
      break;
    default:
      break;

  }

  return score
}
