import Header from './header.js'
import { useEffect } from 'react'
import scoreService from './../services/scores'

const Beginner = ({ functions }) => {
  const [
    setWidth,
    setHeight,
    setMineCount,
    initializeGrid
  ] = functions
  const width = 9
  const height = 9
  const mineCount = 10

  const sortAndSetScore = (scores) => {
    scores.sort((a, b) => a.time - b.time)
    setTopScores( scores )
  }
  useEffect(() => {
    scoreService.getBeginner()
      .then(scores => sortAndSetScore(scores))
    }, [])

  return (
    <>
      <Header
        width={width} height={height} mineCount={mineCount}
        functions={[
          setWidth,
          setHeight,
          setMineCount,
          initializeGrid
        ]}
      />
    </>
  )
}

export default Beginner
