import Header from './header.js'
import Leaderboard from './leaderboard.js'
import { useState, useEffect } from 'react'
import scoreService from './../services/scores'

const Beginner = ({ functions }) => {
  const [topScores, setTopScores] = useState([{ username:'default-ukko', time: 123, id: 1 }])

  const sortAndSetScore = (scores) => {
    scores.sort((a, b) => a.time - b.time)
    setTopScores( scores )
  }
  useEffect(() => { //Get scores

    scoreService.getBeginner()
      .then(scores => sortAndSetScore(scores))
  }, [])

  const [
    setWidth,
    setHeight,
    setMineCount,
    initializeGrid
  ] = functions
  // const width = 9
  // const height = 9
  // const mineCount = 10


  return (
    <>
      <div>adsasd</div>
      <Leaderboard scores={topScores}/>
      <div style={{ marginInline:"auto", width: "fit-content" }} className="pb-4">
        <button style={{width:"150px"}}
          onClick={ initializeGrid }
          > New Game / Refresh</button>
      </div>
      {/* <Header
        width={width} height={height} mineCount={mineCount}
        functions={[
          setWidth,
          setHeight,
          setMineCount,
          initializeGrid
        ]}
      /> */}
    </>
  )
}

export default Beginner
