import React from 'react';
import { useState, useEffect, useRef } from 'react';

const Timer = () => {
  const [seconds, setSeconds] = useState(0)


  useEffect(() => {
    handleTime()
    return () => clearInterval()
  }, []);

  const id = useRef()

  const handleTime = () => {
    id.current = setInterval(() =>{
      setSeconds((prev) => prev + 1)
    }, 1000)
  }
  return (
      <div className="timer">
          {seconds}
      </div>
  )
}
export default Timer;
