const GameOverBox = ({ text, username, functions }) => {
  const [setUsername, newGame, saveScore] = functions

  if (text === ''){ 
    return null 
  } else {
    const buttons = text==='Game over!' ? (
      <button onClick={ newGame }>Play again?</button>
    ) : (
        <>
          <h2>Save score to the leaderboard?</h2>
          <input
            type="text"
            value={username}
            name="title"
            onChange={({ target }) => setUsername(target.value)}
          />
          <button onClick={ () => saveScore(username) }>Save</button>
        </>
    )
    return (
      <div className="gameOverBox">
        <div className="gameOverText">
          {text}
        </div>
        {buttons}
      </div>
    )
  }




}

export default GameOverBox
